package p1;

public class Largestnumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	int x=5;
	int y =10;
	int z=15;
	
	if(x>y & x>z)
	{
		System.out.println("x is largest");
	}
		
    else if (y>z & y>x)
    {
	System.out.println("y is largest");
	
	}
	else
	{
		System.out.println("z is largest");
	}
	}
	}
	

